import React from 'react'
import {Card, Avatar,RadioButton} from 'react-native-paper'
import {Text,Image,View, StyleSheet} from 'react-native'

const Header = (props)=>{

const LeftContent = props => <Avatar.Icon onTouchStart={()=> alert('CÃ©sar Henrique Alves Oncala \nEu vou vencer! ðŸ˜„')}  style={{backgroundColor:'black'}} {...props} />
const [checked, setChecked] = React.useState(0);


return(
  <>
      <Card.Title title={props.pokemon?.name.toUpperCase()}
        subtitle={props.pokemon?.general.genus}
        titleStyle={styles.value}
        subtitleStyle={styles.value}
        right={() => <Text style={{
          fontSize:25,
          borderRadius:5,
          marginRight:12,
          backgroundColor:'grey'
          ,color:'white'}}> #{props.pokemon?.general.order} </Text> }
          left={()=>LeftContent({icon:'pokeball', size: 40, color:'white'})}
        />

          <Image style={styles.sprite} source={{uri: props.pokemon?.sprite[checked] }}/>
        
          <View style={{display:'flex', 
            alignItems:'center',
            flexDirection:'row',
            justifyContent:'space-evenly'}}> 
              
              <View style={styles.radioBtn}>
                <Text style={styles.value}> Normal </Text>
                <RadioButton
                theme={{colors:{text:'white'} }}
                value={0}
                status={ checked === 0? 'checked' : 'unchecked' }
                onPress={() => setChecked(0)}
                  />
              </View>

              <View style={styles.radioBtn}>   
                <Text style={styles.value} for='shiny'> Shiny </Text>
                <RadioButton
                  theme={{colors:{text:'white'} }}
                  id='shiny'
                  value={1}
                  status={ checked === 1 ? 'checked' : 'unchecked' }
                  onPress={() => setChecked(1)}
                />
              </View>
            </View>
            </>
        )
}


export default Header;

const styles = StyleSheet.create({

radioBtn:{display:'flex',alignItems:'center',flexDirection:'row', marginTop:5},

  sprite:{
      width:250,
      height:250,
      alignSelf:'center',
      borderRadius:45,
      shadowColor:'black',
   },
    value:{
        color:'#f0f0f0',
        marginLeft:2,
        textShadowColor: 'black',
        textShadowRadius: 6, 
   }

})
