import React, { useState } from 'react'
import PokeCard from './PokeCard/PokeCard.jsx'
import { ScrollView, StyleSheet, View, Text } from 'react-native'
import { Autocomplete } from './Autocomplete'
import {getPokemon} from '../PokemonDataService.jsx'




export const Pokedex = () => {


  const [pokemon, setPokemon] = React.useState(undefined);
  const [textinput, settextinput] = useState('')



  React.useEffect(() => sendRequest(25), [])


  React.useEffect(() => {

    if (textinput.length >= 3)
      sendRequest(textinput.toLowerCase())
  }, [textinput])


  return (
    <>
      <ScrollView>
        <View style={styles.background}>
          <Autocomplete settextinput={settextinput} textinput={textinput} />
          <PokeCard pokemon={pokemon} />
        </View>
      </ScrollView>
    </>
  )
}

const styles = StyleSheet.create({

  background: {
    backgroundColor: '#1e1e1e',
    position: 'relative'
  }
})
